import React, { useState, useEffect } from 'react';
import Header from './Header';
import { useTranslation } from 'react-i18next';

export default function SupportPage() {
  const [dark, setDark] = useState(false);
  const { t } = useTranslation();

  useEffect(() => {
    document.title = t('support');
  }, [t]);

  return (
    <div className={dark ? 'dark bg-gray-900 text-gray-100 min-h-screen' : 'bg-white text-gray-900 min-h-screen'}>
      <Header dark={dark} setDark={setDark} />


{/* Main content */}
<main className="container mx-auto px-6 py-12">
  <h2 className="text-4xl font-extrabold mb-8 text-center">{t('support', 'Support')}</h2>
  <p className="text-lg opacity-80 text-center max-w-2xl mx-auto mb-12">
    {t(
      'supportDescription',
      'If you have any questions, issues, or feedback, please contact us.'
    )}
  </p>

  <div className="grid md:grid-cols-3 gap-8">
    {/* Email Card */}
    <div className="p-6 rounded-xl border dark:border-gray-700 bg-white dark:bg-gray-800 shadow hover:shadow-lg transition">
      <div className="text-indigo-600 text-3xl mb-4">📧</div>
      <h3 className="font-semibold text-xl mb-2">{t('email', 'Email')}</h3>
      <p className="text-sm opacity-75">
        <a href="mailto:support@dailypush.app" className="underline hover:text-indigo-600">
          support@dailypush.app
        </a>
      </p>
    </div>

    {/* FAQ Card */}
    <div className="p-6 rounded-xl border dark:border-gray-700 bg-white dark:bg-gray-800 shadow hover:shadow-lg transition">
      <div className="text-indigo-600 text-3xl mb-4">❓</div>
      <h3 className="font-semibold text-xl mb-2">{t('faq', 'FAQ')}</h3>
      <p className="text-sm opacity-75">
        <a href="#" className="underline hover:text-indigo-600">
          {t('faqLink', 'View our Frequently Asked Questions')}
        </a>
      </p>
    </div>

    {/* Contact Form Card */}
    <div className="p-6 rounded-xl border dark:border-gray-700 bg-white dark:bg-gray-800 shadow hover:shadow-lg transition">
      <div className="text-indigo-600 text-3xl mb-4">📝</div>
      <h3 className="font-semibold text-xl mb-2">{t('contactForm', 'Contact Form')}</h3>
      <p className="text-sm opacity-75">
        <a href="#" className="underline hover:text-indigo-600">
          {t('contactFormLink', 'Fill out our contact form')}
        </a>
      </p>
    </div>
  </div>
</main>



      {/* Footer */}
      <footer className="mt-20 text-center text-sm opacity-60 py-6 border-t dark:border-gray-700">
        © {new Date().getFullYear()} Daily Push.
      </footer>
    </div>
  );
}
