import React, { useContext, useEffect } from 'react';
import Header from './Header';
import { useTranslation } from 'react-i18next';
import { AppContext } from './AppContext';

export default function PrivacyPage() {
  const { dark } = useContext(AppContext);
  const { t } = useTranslation();

  useEffect(() => {
    document.title = t('privacyPage.title');
  }, [t]);

  return (
    <div className={dark ? 'dark bg-gray-900 text-gray-100 min-h-screen' : 'bg-white text-gray-900 min-h-screen'}>
      <Header />

      <main className="container mx-auto px-6 py-12 max-w-3xl">
        <h1 className="text-3xl font-bold mb-6">{t('privacyPage.title')}</h1>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('privacyPage.collectedTitle')}</h2>
        <p className="mb-2">{t('privacyPage.collectedIntro')}</p>
        <p className="whitespace-pre-line mb-4">{t('privacyPage.collectedList')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('privacyPage.usageTitle')}</h2>
        <p className="mb-4">{t('privacyPage.usageText')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('privacyPage.sharingTitle')}</h2>
        <p className="mb-4">{t('privacyPage.sharingText')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('privacyPage.rightsTitle')}</h2>
        <p className="mb-4">{t('privacyPage.rightsText')}</p>
      </main>

      <footer className="mt-20 text-center text-sm opacity-60 py-6 border-t dark:border-gray-700">
        Â© {new Date().getFullYear()} Daily Push.
      </footer>
    </div>
  );
}
