import React, { useState, useEffect } from 'react';
import Header from './Header';
import { useTranslation } from 'react-i18next';

export default function TermsPageFR() {
  const [dark, setDark] = useState(false);
  const { t } = useTranslation();

  useEffect(() => {
    document.title = t('termsPage.title');
  }, [t]);

  return (
    <div className={dark ? 'dark bg-gray-900 text-gray-100 min-h-screen' : 'bg-white text-gray-900 min-h-screen'}>
      <Header dark={dark} setDark={setDark} />

      <main className="container mx-auto px-6 py-12 max-w-3xl">
        <h1 className="text-3xl font-bold mb-6">{t('termsPage.title')}</h1>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('termsPage.section1Title')}</h2>
        <p className="mb-4">{t('termsPage.section1Text')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('termsPage.section2Title')}</h2>
        <p className="mb-4">{t('termsPage.section2Text')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('termsPage.section3Title')}</h2>
        <p className="mb-4">{t('termsPage.section3Text')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('termsPage.section4Title')}</h2>
        <p className="mb-4">{t('termsPage.section4Text')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('termsPage.section5Title')}</h2>
        <p className="mb-4">{t('termsPage.section5Text')} <a href="mailto:contact.dailypushproton.me@proton.me" className="text-indigo-600 hover:underline">contact.dailypushproton.me@proton.me</a></p>
      </main>

      <footer className="mt-20 text-center text-sm opacity-60 py-6 border-t dark:border-gray-700">
        Â© {new Date().getFullYear()} Daily Push.
      </footer>
    </div>
  );
}
