import React, { useState, useEffect } from 'react';
import LanguageSwitcher from './LanguageSwitcher';
import logo from './assets/push-up-128.png';
import { useTranslation } from 'react-i18next';

export default function SupportPage() {
  const [dark, setDark] = useState(false);
  const { t } = useTranslation();

  useEffect(() => {
    document.title = t('support');
  }, [t]);

  return (
    <div className={dark ? 'dark bg-gray-900 text-gray-100 min-h-screen' : 'bg-white text-gray-900 min-h-screen'}>
      {/* Header */}
      <header className="container mx-auto px-6 py-6 flex items-center justify-between">
        <div className="flex items-center gap-3">
          <a href="index.html" className="w-10 h-10 rounded-md overflow-hidden">
            <img src={logo} alt="Daily Push Logo" className="w-full h-full object-cover" />
          </a>
          <div>
            <h1 className="text-lg font-semibold">Daily Push</h1>
            <p className="text-xs opacity-70">From 0 to 100 Push‑Ups</p>
          </div>
        </div>

        <div className="flex items-center gap-4">
          <nav className="flex gap-3 text-sm">
            <a href="index.html" className="hover:underline">{t('home')}</a>
            <a href="support.html" className="hover:underline font-semibold">{t('support')}</a>
            <a href="privacy.html" className="hover:underline">{t('privacy')}</a>
            <a href="terms.html" className="hover:underline">{t('terms')}</a>
          </nav>

          <LanguageSwitcher />

          <button
            onClick={() => setDark(!dark)}
            className="px-3 py-2 rounded-md border dark:border-gray-700 text-sm"
          >
            {dark ? t('lightMode') : t('darkMode')}
          </button>
        </div>
      </header>

      {/* Main content */}
      <main className="container mx-auto px-6 py-12">
        <h2 className="text-3xl font-bold mb-6">{t('support')}</h2>
        <p className="text-lg opacity-80">
          {t('supportDescription', 'If you have any questions, issues, or feedback, please contact us.')}
        </p>

        <ul className="mt-6 space-y-4">
          <li>
            <strong>Email:</strong> support@dailypush.app
          </li>
          <li>
            <strong>FAQ:</strong> <a href="faq.html" className="text-indigo-600 hover:underline">Visit our FAQ page</a>
          </li>
          <li>
            <strong>Contact form:</strong> <a href="contact.html" className="text-indigo-600 hover:underline">Fill out the form</a>
          </li>
        </ul>
      </main>

      {/* Footer */}
      <footer className="mt-20 text-center text-sm opacity-60 py-6 border-t dark:border-gray-700">
        © {new Date().getFullYear()} Daily Push.
      </footer>
    </div>
  );
}
