import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import logo from './assets/push-up-128.png';

export default function Header({ dark, setDark }) {
  const { i18n, t } = useTranslation();
  const [langOpen, setLangOpen] = useState(false);

  const changeLang = (lang) => {
    i18n.changeLanguage(lang);
    setLangOpen(false);
  };

  return (
    <header className="container mx-auto px-6 py-6 flex items-center justify-between">
      <div className="flex items-center gap-3">
        <div className="w-10 h-10 rounded-md overflow-hidden">
          <img src={logo} alt="Daily Push Logo" className="w-full h-full object-cover" />
        </div>
        <div>
          <h1 className="text-lg font-semibold">Daily Push</h1>
          <p className="text-xs opacity-70">{t('subtitle')}</p>
        </div>
      </div>

      <div className="flex items-center gap-4 relative">
        {/* Dark Mode */}
        <button
          onClick={() => setDark(!dark)}
          className="px-3 py-2 rounded-md border dark:border-gray-700 text-sm"
        >
          {dark ? (i18n.language === 'fr' ? 'Mode Clair' : 'Light Mode') : (i18n.language === 'fr' ? 'Mode Sombre' : 'Dark Mode')}
        </button>

        {/* Dropdown Langue */}
        <div className="relative inline-block text-left">
          <button
            onClick={() => setLangOpen(!langOpen)}
            className="px-3 py-2 rounded-md border dark:border-gray-700 text-sm"
          >
            {i18n.language === 'fr' ? 'Français' : 'English'}
          </button>

          {langOpen && (
            <div className="absolute right-0 mt-2 w-32 rounded-md shadow-lg bg-white dark:bg-gray-800 border dark:border-gray-700 z-10">
              <button
                onClick={() => changeLang('en')}
                className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                English
              </button>
              <button
                onClick={() => changeLang('fr')}
                className="block w-full text-left px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700"
              >
                Français
              </button>
            </div>
          )}
        </div>

        <a href="#download" className="inline-block px-4 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-md text-sm">{t('download')}</a>
      </div>
    </header>
  );
}
