import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';

import feature1 from './assets/feature1.png';
import feature2 from './assets/feature2.png';
import feature3 from './assets/feature3.png';

export default function DiscoverApp() {
  return (
    <section className="py-16 bg-gray-100">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold text-indigo-600">Discover the App</h2>
        <p className="mt-4 text-lg text-gray-700">
          Experience the features that make our app unique and user-friendly.
        </p>

        <div className="mt-8">
          <Swiper
            spaceBetween={20}
            slidesPerView={1}
            breakpoints={{
              640: { slidesPerView: 2 },
              1024: { slidesPerView: 3 },
            }}
            loop={true}
          >
            <SwiperSlide>
              <img src={feature1} alt="Feature 1" className="rounded-lg shadow-lg" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={feature2} alt="Feature 2" className="rounded-lg shadow-lg" />
            </SwiperSlide>
            <SwiperSlide>
              <img src={feature3} alt="Feature 3" className="rounded-lg shadow-lg" />
            </SwiperSlide>
          </Swiper>
        </div>

        <div className="mt-12 flex justify-center gap-6">
          <a href="#" className="px-6 py-3 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-700">
            App Store
          </a>
          <a href="#" className="px-6 py-3 bg-green-600 text-white rounded-lg shadow-md hover:bg-green-700">
            Google Play
          </a>
        </div>
      </div>
    </section>
  );
}
