import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import { Navigation, Pagination } from 'swiper/modules';
import { useTranslation } from 'react-i18next';

import preview1 from './assets/preview1.png';
import preview2 from './assets/preview2.png';
import preview3 from './assets/preview3.png';
import preview4 from './assets/preview4.png';
import preview5 from './assets/preview5.png';

export default function DiscoverApp() {
    const { t } = useTranslation();

    return (
    <section className="py-16">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold text-indigo-600">Discover the App</h2>

        <div className="mt-8">
        <Swiper
          modules={[Pagination, Navigation]}
          pagination={{ clickable: true }}
          navigation
          loop={true}
          spaceBetween={16}
          slidesPerView={1}
          breakpoints={{
            640: { slidesPerView: 2, spaceBetween: 20 },
            1024: { slidesPerView: 3, spaceBetween: 24 },
            1280: { slidesPerView: 5, spaceBetween: 32 },
          }}
        >
            <SwiperSlide><img src={preview1} alt="Preview 1" className="rounded-lg shadow-lg" /></SwiperSlide>
            <SwiperSlide><img src={preview2} alt="Preview 2" className="rounded-lg shadow-lg" /></SwiperSlide>
            <SwiperSlide><img src={preview3} alt="Preview 3" className="rounded-lg shadow-lg" /></SwiperSlide>
            <SwiperSlide><img src={preview4} alt="Preview 4" className="rounded-lg shadow-lg" /></SwiperSlide>
            <SwiperSlide><img src={preview5} alt="Preview 5" className="rounded-lg shadow-lg" /></SwiperSlide>
        </Swiper>
        </div>
      </div>
    </section>
  );
}
