import React, { useState, useEffect } from 'react';
import Header from './Header';
import { useTranslation } from 'react-i18next';

export default function PrivacyPage() {
  const [dark, setDark] = useState(false);
  const { t } = useTranslation();

  useEffect(() => {
    document.title = t('privacy', 'Privacy Policy');
  }, [t]);

  return (
    <div className={dark ? 'dark bg-gray-900 text-gray-100 min-h-screen' : 'bg-white text-gray-900 min-h-screen'}>
      <Header dark={dark} setDark={setDark} />

      <main className="container mx-auto px-6 py-12 max-w-3xl">
        <h1 className="text-3xl font-bold mb-6">{t('privacy', 'Privacy Policy')}</h1>
        <p className="mb-4">{t('privacyIntro', "Daily Push is committed to protecting your privacy. Here's what you need to know:")}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('dataCollected', '1. Data Collected')}</h2>
        <p className="mb-2">{t('dataCollectedDesc', 'We collect only the data necessary for the app to function, such as:')}</p>
        <ul className="list-disc pl-6 mb-4">
          <li>{t('accountInfo', 'Account information (email, user ID)')}</li>
          <li>{t('trainingData', 'Training data (e.g., number of push-ups)')}</li>
          <li>{t('subscriptionData', 'Subscription data (managed by Apple/Google)')}</li>
        </ul>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('howUseData', '2. How We Use Data')}</h2>
        <p className="mb-4">{t('howUseDataDesc', 'We use data to provide and improve the app, track your progress, and manage your subscription.')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('dataSharing', '3. Data Sharing')}</h2>
        <p className="mb-4">{t('dataSharingDesc', 'We do not sell your data. Service providers (hosting, limited analytics) may access necessary data.')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('yourRights', '4. Your Rights')}</h2>
        <p className="mb-4">{t('yourRightsDesc', 'You can request deletion or export of your data at any time by contacting us.')}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-2">{t('contact', 'Contact')}</h2>
        <p>
          {t('contactDesc', 'For privacy inquiries, reach out to:')}{' '}
          <a className="text-indigo-600 underline" href="mailto:contact.dailypushproton.me@proton.me">
            contact.dailypushproton.me@proton.me
          </a>
        </p>
      </main>

      <footer className="mt-20 text-center text-sm opacity-60 py-6 border-t dark:border-gray-700">
        Â© {new Date().getFullYear()} Daily Push.
      </footer>
    </div>
  );
}
