import React, { useState, useEffect } from 'react';
import Header from './Header';
import { useTranslation } from 'react-i18next';

export default function SupportPage() {
  const [dark, setDark] = useState(false);
  const { t } = useTranslation();

  useEffect(() => {
    document.title = t('support');
  }, [t]);

  return (
    <div className={dark ? 'dark bg-gray-900 text-gray-100 min-h-screen' : 'bg-white text-gray-900 min-h-screen'}>
      <Header dark={dark} setDark={setDark} />


      {/* Main content */}
      <main className="container mx-auto px-6 py-12">
        <h2 className="text-3xl font-bold mb-6">{t('support')}</h2>
        <p className="text-lg opacity-80">
          {t('supportDescription', 'If you have any questions, issues, or feedback, please contact us.')}
        </p>

        <ul className="mt-6 space-y-4">
          <li>
            <strong>Email:</strong> support@dailypush.app
          </li>
          <li>
            <strong>FAQ:</strong> <a href="faq.html" className="text-indigo-600 hover:underline">Visit our FAQ page</a>
          </li>
          <li>
            <strong>Contact form:</strong> <a href="contact.html" className="text-indigo-600 hover:underline">Fill out the form</a>
          </li>
        </ul>
      </main>

      {/* Footer */}
      <footer className="mt-20 text-center text-sm opacity-60 py-6 border-t dark:border-gray-700">
        Â© {new Date().getFullYear()} Daily Push.
      </footer>
    </div>
  );
}
